// src/components/CourtOption.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/CourtOption.css';

function CourtOption() {
  return (
    <div className="court-option">
      <div className="container">
        <h1 className="title">Choose Your Court Option</h1>
        <div className="options">
          <Link to="/district" className="court-button district">
            District Court
          </Link>
          <Link to="/high" className="court-button high">
            High Court
          </Link>
          <Link to="/supreme" className="court-button supreme">
            Supreme Court
          </Link>
        </div>
        <div className="cta-section">
          <p className="cta-text">Explore the available judgments and upload them with ease.</p>
        </div>
      </div>
    </div>
  );
}

export default CourtOption;
