// src/components/CourtOptions.js
import React from 'react';
import { useParams, Link } from 'react-router-dom';

function CourtOptions() {
  const { state } = useParams();  // Get the selected state from URL

  return (
    <div className="court-options">
      <h1>Choose an Option for {state.replace(/-/g, ' ')}</h1>
      <div className="options">
        <Link to={`/view-judgment/${state}`} className="option-btn">
          View Judgment
        </Link>
        <Link to={`/upload-judgment/${state}`} className="option-btn">
          Upload Judgment
        </Link>
      </div>
    </div>
  );
}

export default CourtOptions;
