import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ViewJudgment.css';

function ViewJudgment() {
  const { state } = useParams(); // Access the state parameter
  const [searchFilters, setSearchFilters] = useState({
    neutralCitation: '',
    category: '',
    sittingJudge: '',
    formerJudge: '',
    caseNumber: '',
    freeText: '',
    judgmentDate: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchFilters({ ...searchFilters, [name]: value });
  };

  const handleSearch = () => {
    console.log('Searching with filters:', searchFilters);
    // Simulate fetching data based on filters
  };

  return (
    <div className="view-judgment-container">
      <h1>View Judgments for {state}</h1>
      <p>Search judgments using various criteria from {state}.</p>
      <form className="search-form">
        <input
          type="text"
          name="neutralCitation"
          placeholder="Neutral Citation"
          value={searchFilters.neutralCitation}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="text"
          name="category"
          placeholder="Category"
          value={searchFilters.category}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="text"
          name="sittingJudge"
          placeholder="Sitting Judge's Name"
          value={searchFilters.sittingJudge}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="text"
          name="formerJudge"
          placeholder="Former Judge's Name"
          value={searchFilters.formerJudge}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="text"
          name="caseNumber"
          placeholder="Case Number"
          value={searchFilters.caseNumber}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="text"
          name="freeText"
          placeholder="Free Text Search"
          value={searchFilters.freeText}
          onChange={handleInputChange}
          className="form-input"
        />

        <input
          type="date"
          name="judgmentDate"
          value={searchFilters.judgmentDate}
          onChange={handleInputChange}
          className="form-input"
        />

        <button
          type="button"
          className="cta-button"
          onClick={handleSearch}
        >
          Search
        </button>
      </form>
    </div>
  );
}

export default ViewJudgment;
