// In HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';
import districtImage from '../assets/images/image2.jpeg';
import highCourtImage from '../assets/images/image3.jpeg';
import supremeCourtImage from '../assets/images/supreme.jpeg';
import Navbar from './Navbar';  // Adjust the import path to match your folder structure

function HomePage() {
  return (
    <div className="homepage">
      {/* Include Navbar */}
      <Navbar />

      {/* Hero Section */}
      <section className="hero-section">
        <h1>Welcome to Centralized Court Judgments</h1>
        <p>Access High Court Judgments Easily and Efficiently</p>
        <div className="search-bar">
          <input type="text" placeholder="Search by case title, court, or date..." />
          <button>Search</button>
        </div>
      </section>

      {/* Quick Links Section */}
      <section className="court-links-section">
        <h2>Explore Courts</h2>
        <div className="court-options-grid">
          <Link to="/district" className="court-option">
            <img src={districtImage} alt="District Court" />
            <div className="overlay">
              <div className="text">District Court</div>
            </div>
          </Link>
          <Link to="/high" className="court-option">
            <img src={highCourtImage} alt="High Court" />
            <div className="overlay">
              <div className="text">High Court</div>
            </div>
          </Link>
          <Link to="/supreme" className="court-option">
            <img src={supremeCourtImage} alt="Supreme Court" />
            <div className="overlay">
              <div className="text">Supreme Court</div>
            </div>
          </Link>
        </div>
      </section>
    </div>
  );
}

export default HomePage;
