import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/ViewJudgment.css';

function ViewJudgment() {
  const { state } = useParams(); // Access the state parameter
  const [searchTerm, setSearchTerm] = useState('');
  const [judgments, setJudgments] = useState([]); // Placeholder for fetched data

  const handleSearch = () => {
    // Simulate fetching data based on search term
    const mockData = [
      { id: 1, title: `Judgment 1 from ${state}`, description: 'Details of judgment 1...' },
      { id: 2, title: `Judgment 2 from ${state}`, description: 'Details of judgment 2...' },
    ];
    setJudgments(mockData);
  };

  return (
    <div className="view-upload-judgment">
      <h1>View Judgments for {state}</h1>
      <p>Search for judgments in {state} by keywords or case details.</p>
      <div className="search-container">
        <input
          type="text"
          className="search-bar"
          placeholder="Enter case details or keywords"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="cta-button" onClick={handleSearch}>
          Search
        </button>
      </div>
      {judgments.length > 0 && (
        <div className="judgment-list">
          {judgments.map((judgment) => (
            <div key={judgment.id} className="judgment-item">
              <h3>{judgment.title}</h3>
              <p>{judgment.description}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default ViewJudgment;
