// src/components/UploadJudgment.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/ViewUploadJudgment.css'; // Import CSS file

function UploadJudgment() {
  const { state } = useParams();
  const [file, setFile] = useState(null);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = () => {
    // Add your file upload logic here
    console.log('File uploaded:', file);
  };

  return (
    <div className="upload-judgment">
      <h1>Upload Judgment for {state.replace(/-/g, ' ')}</h1>
      <div className="upload-form">
        <input
          type="file"
          className="file-input"
          onChange={handleFileChange}
        />
        <button className="upload-btn" onClick={handleUpload}>
          Upload Judgment
        </button>
      </div>
    </div>
  );
}

export default UploadJudgment;
