// src/components/StateSelection.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/StateSelection.css';

// List of Indian States
const statesList = [
  "Andhra Pradesh", "Arunachal Pradesh", "Assam", "Bihar", "Chhattisgarh", "Goa",
  "Gujarat", "Haryana", "Himachal Pradesh", "Jharkhand", "Karnataka", "Kerala", 
  "Madhya Pradesh", "Maharashtra", "Manipur", "Meghalaya", "Mizoram", "Nagaland", 
  "Odisha", "Punjab", "Rajasthan", "Sikkim", "Tamil Nadu", "Telangana", "Tripura", 
  "Uttar Pradesh", "Uttarakhand", "West Bengal", "Andaman and Nicobar Islands", 
  "Chandigarh", "Dadra and Nagar Haveli and Daman and Diu", "Lakshadweep", "Delhi", 
  "Puducherry", "Ladakh", "Lakshadweep"
];

function StateSelection({ courtType }) {
  const [searchTerm, setSearchTerm] = useState("");
  const [filteredStates, setFilteredStates] = useState(statesList);

  const handleSearch = (event) => {
    const term = event.target.value;
    setSearchTerm(term);

    // Filter the states based on the search term
    const filtered = statesList.filter(state => 
      state.toLowerCase().includes(term.toLowerCase().trim())  // Ensure leading/trailing spaces are removed
    );
    setFilteredStates(filtered);
  };

  return (
    <div className="state-selection">
      <h1>Select a State for {courtType === 'district' ? 'District Court' : 'High Court'} Judgments</h1>

      {/* Search Bar */}
      <input 
        type="text" 
        className="search-bar" 
        placeholder="Search for a state..." 
        value={searchTerm}
        onChange={handleSearch}
      />

      <div className="states-grid">
        {/* Display filtered states */}
        {filteredStates.map(state => (
          <div className="state-item" key={state}>
            <h3>{state}</h3>

            {/* Only show relevant court link */}
            <Link 
              to={`/court/${courtType}/${state.toLowerCase().replace(/\s+/g, '-')}`} 
              className="state-link"
            >
              Select {courtType === 'district' ? 'District Court' : 'High Court'}
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StateSelection;
