// src/components/UploadJudgment.js
import React, { useState } from 'react';
import '../styles/ViewUploadJudgment.css'; // Import the CSS file

function UploadJudgment() {
  const [judgmentFile, setJudgmentFile] = useState(null);

  const handleFileChange = (e) => {
    setJudgmentFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can later add logic to handle the file upload
    console.log('File uploaded:', judgmentFile);
  };

  return (
    <div className="view-upload-judgment">
      <h1>Upload Judgment</h1>
      <form className="upload-form" onSubmit={handleSubmit}>
        <input
          type="file"
          accept=".pdf,.docx,.txt" // You can restrict the file types as needed
          onChange={handleFileChange}
          required
        />
        <button type="submit">Upload Judgment</button>
      </form>
    </div>
  );
}

export default UploadJudgment;
