// src/components/CourtOptions.js
import React from 'react';
import { Link, useParams } from 'react-router-dom';
import '../styles/CourtOption.css';

function CourtOptions() {
  const { state } = useParams(); // Getting the state parameter from the URL

  return (
    <div className="court-options">
      <div className="container">
        <h1>Options for {state} Court</h1>
        <div className="options">
          <Link to={`/view-judgment/${state}`} className="cta-button">
            View Judgment
          </Link>
          <Link to={`/upload-judgment/${state}`} className="cta-button">
            Upload Judgment
          </Link>
        </div>
      </div>
    </div>
  );
}

export default CourtOptions;
