import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Navbar.css'; // Link the CSS file for styling

function Navbar() {
  const [isMobile, setIsMobile] = useState(false);

  // Toggle the navbar on mobile view
  const handleToggle = () => setIsMobile(!isMobile);

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Delhi High Court</Link>
      </div>
      <ul className={`navbar-menu ${isMobile ? 'active' : ''}`}>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/search">Judgments</Link></li>
        <li><Link to="/notices">Notices</Link></li>
        <li><Link to="/contact">Contact Us</Link></li>
      </ul>
      <div className="navbar-toggle" onClick={handleToggle}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
    </nav>
  );
}

export default Navbar;
