// src/components/UploadJudgment.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/ViewUploadJudgment.css';

function UploadJudgment() {
  const { state } = useParams(); // Get the state from URL
  const [judgmentFile, setJudgmentFile] = useState(null);

  const handleFileChange = (e) => {
    setJudgmentFile(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Logic for handling file upload can be added here
    console.log('File uploaded for', state, 'court:', judgmentFile);
  };

  return (
    <div className="view-upload-judgment">
      <h1>Upload Judgment for {state} Court</h1>
      <form className="upload-form" onSubmit={handleSubmit}>
        <input
          type="file"
          accept=".pdf,.docx,.txt"
          onChange={handleFileChange}
          required
        />
        <button type="submit">Upload Judgment</button>
      </form>
    </div>
  );
}

export default UploadJudgment;
