// src/components/ViewJudgment.js
import React from 'react';
import { useParams } from 'react-router-dom';
import '../styles/ViewUploadJudgment.css';

function ViewJudgment() {
  const { state } = useParams(); // Get the state from URL

  return (
    <div className="view-upload-judgment">
      <h1>View Judgment for {state} Court</h1>
      <p>This page will show all the available judgments for your selected {state} court.</p>
      {/* Logic to display judgments for the selected state */}
      <a href="#" className="cta-button">View Judgments</a>
    </div>
  );
}

export default ViewJudgment;
