// src/components/CourtOption.js
import React from 'react';
import { Link } from 'react-router-dom';
import '../styles/CourtOption.css'; // Import the new CSS file

function CourtOption() {
  return (
    <div className="court-option">
      <div className="container">
        <h1 className="title">Choose Your Court Option</h1>
        <div className="options">
          <Link to="/view-judgment" className="court-button view">
            View Judgment
          </Link>
          <Link to="/upload-judgment" className="court-button upload">
            Upload Judgment
          </Link>
        </div>
        <div className="cta-section">
          <p className="cta-text">Discover the best way to manage court data and judgments in one place.</p>
        </div>
      </div>
    </div>
  );
}

export default CourtOption;
